import React from 'react';
import { useState } from 'react';
import { View, Text, Button, TextInput, StyleSheet, TouchableOpacity } from 'react-native';

export default class Start extends React.Component {
  constructor(props) {
    super(props)
    useState
    this.state = { name: '',
      selectedBackground: ''
    };
  }


  render() {
    const [selected, setSelected] = useState(false);

    return (
      <View className="backgroundStyle" style={styles.container}>
      <View style={{flex: 1, flexDirection: 'column'}}>
      <TouchableOpacity
        onPress={() => setSelected(!selected)}
        style={{ backgroundColor: selected ? 'powderblue' : 'powderblue' }}
      >

      <View style={{ width: 50, height: 50, backgroundColor: 'powderblue', borderRadius: '50'}} />

      </TouchableOpacity>


      <View style={{width: 50, height: 50, backgroundColor: 'skyblue', borderRadius: '50'}} />
      <View style={{width: 50, height: 50, backgroundColor: 'steelblue', borderRadius: '50'}} />
      </View>
        <View style={{flex:1, justifyContent: 'center', alignItems: 'center'}}>
        <Text>Username</Text>
        <TextInput
         style={{height: 40, borderColor: 'gray', borderWidth: 1}}
         onChangeText={(name) => this.setState({name})}
         value={this.state.name}
         placeholder='Type here ...'
       />
       <Text>You wrote: {this.state.name}</Text>
        <Button
          title="Go to Chat"
          onPress={() => this.props.navigation.navigate('Chat', { name: this.state.name })} />
      </View>
      </View>
      
    )
  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1
  }
});
