import React from 'react';
import { View, Text, Button, TextInput, StyleSheet, ImageBackground } from 'react-native';


export default class Start extends React.Component {
  constructor(props) {
    super(props)
    this.state = { name: '',

    };
  }

  static navigationOptions = ({ navigation }) => {
    return {
      title: `${navigation.state.params.name}'s Chat`,
    };
  };
  
render() {
  const image = { uri: "https://i.pinimg.com/originals/bc/47/67/bc47670a93657e3c7bceac136555a818.jpg"};
    return (
    <View className="backgroundStyle" style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
        <View style={{ flex:1, justifyContent: 'flex-start', justifyContent: 'space-between', alignItems: 'center' }}>
          <Text style={styles.head}>STAY CONNECTED</Text>
            <TextInput
            style={{height: 40, width: 250, borderColor: 'gray', borderWidth: 1, backgroundColor: 'white'}}
            onChangeText={(name) => this.setState({name})}
            value={this.state.name}
            placeholder='Type Username Here'
            />
        <View style={{ alignSelf: 'stretch'}}>
          <Button
            title="Go to Chat" color='#FF364E'
            onPress={() => this.props.navigation.navigate('Chat', { name: this.state.name })} />
          </View>
        </View>
      </ImageBackground>
    </View>
    )
  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    height: 'auto'
   },
   image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  head: {
    marginTop:'1em',
    fontSize: '40px',
    color: '#3F3F3F',
    fontWeight: 'bold',
    textShadow: '1px 1px #616060',
},
});
