import React from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

export default class Chat extends React.Component {
  state={
    backgroundColor: 'powderblue',
    backgroundColor2: 'skyblue',
    pressed: false,
  };

  changeColor(){
    if(!this.state.pressed){
       this.setState({ pressed: true,backgroundColor: 'red', backgroundColor2: 'black'});
    } else {
      this.setState({ pressed: false, backgroundColor: 'black' ,backgroundColor2: 'red'});
    }
  }

  render() {
    let name = this.props.route.params.name; // OR ...
    // let { name } = this.props.route.params;
    this.props.navigation.setOptions({ title: name });

     return (
  <View className="backgroundStyle" style={styles.container}>   

    <View style={{flex:3, justifyContent: 'center', alignItems: 'center'}}>
        <Button
          title="Go to Start"
          onPress={() => this.props.navigation.navigate('Start')} />
    </View>
  </View>
    );
  };
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white'
   },
   redBack: {
    flex: 1,
    backgroundColor: 'powderblue',
    display: 'cover'
   }
});
