import React from 'react';
import { useState } from 'react';
import { View, Text, Button, TextInput, StyleSheet, ImageBackground } from 'react-native';


export default class Start extends React.Component {
  constructor(props) {
    super(props)
    this.state = { name: '',

    };
  }

  render() {

    const image = { uri: "https://plusonestrategy.com/wp-content/uploads/2014/09/Member-Services-Icon.jpg" };

    return (

  <View className="backgroundStyle" style={styles.container}>
    <ImageBackground source={image} style={styles.image}>
    <View style={{ flex:2, justifyContent: 'center', alignItems: 'center'}}>
        <Text style={styles.text}>Username</Text>
        <TextInput
         style={{height: 40, width: 150, borderColor: 'gray', borderWidth: 1, backgroundColor: 'white'}}
         onChangeText={(name) => this.setState({name})}
         value={this.state.name}
         placeholder='Type here ...'
       />
        <Button
          title="Go to Chat" color='#FF364E' width='200px'
          onPress={() => this.props.navigation.navigate('Chat', { name: this.state.name })} />
      </View>
      </ImageBackground>
  </View>
    )
  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white'
   },
   image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
   text: {
     fontSize: '18px',
     margin: '0.8em'
   },
    redBack: {
    flex: 1,
    backgroundColor: 'powderblue',
    display: 'cover'
    },
});
