import React from 'react';
import { View, Button, Text, StyleSheet, KeyboardAvoidingView, Platform, Flatlist } from 'react-native';
import { GiftedChat, Bubble } from 'react-native-gifted-chat';
// import firebase from 'firebase';

const firebase = require('firebase');
require('firebase/firestore');

export default class Chat extends React.Component {
    constructor() {
      super();
      this.state = {
        messages: [],
        uid: 0,
        loggenInText: 'Please wait, Logging in..'
      }

if (!firebase.apps.length){
  firebase.initializeApp({
  apiKey: "AIzaSyDBiw9HcxkieYg04FpvoQryDAj7U2Ht5-E",
  authDomain: "jw-chat-f1039.firebaseapp.com",
  projectId: "jw-chat-f1039",
  storageBucket: "jw-chat-f1039.appspot.com",
  messagingSenderId: "1089230184479",
  appId: "1:1089230184479:web:581fa3509e59c7cd0b752a",
  measurementId: "G-TE01CF40LR"
  });
  this.referenceChatMessagelistUser = null ;
  }
}

componentDidMount() {
  // creating a references to shoppinglists collection
  this.referenceChatMessages = 
  firebase.firestore()
  .collection('shoppinglists');

  this.referenceChatMessages = firebase.firestore().collection('shoppinglists');
  
  // listen to authentication events
  this.authUnsubscribe = firebase.auth().onAuthStateChanged(async (user) => {
    if (!user) {
      await firebase.auth().signInAnonymously();
    }
    //update user state with currently active user data
    this.setState({
      uid: user.uid,
      loggedInText: 'Hello there',
    });

    // create a reference to the active user's documents (shopping lists)
    this.referenceChatMessages = firebase.firestore("messages")
    .collection('shoppinglists').where("uid", "==", this.state.uid);
    // listen for collection changes for current user 
    this.unsubscribeListUser = this.referenceChatMessages.onSnapshot(this.onCollectionUpdate);

    

  });
}
      
// adding a users with ID name and avatar
// componentDidMount() {
//   this.setState({
//     messages: [
//       {
//         _id: 1,
//           text: 'Hello developer',
//           createdAt: new Date(),
//         user: {
//           _id: 2,
//           name: 'React Native',dfcsd
//           avatar: 'https://placeimg.com/140/140/any',
//         },
//     },
//       {_id: 2,
//         text: 'This is a system message',
//         createdAt: new Date(),
//         system: true,
//       },
//     ]
//   })
// }

componentDidMount() {
  this.referenceChatMessages = firebase.firestore().collection('shoppinglists');
  this.unsubscribe = this.referenceChatMessages.onSnapshot(this.onCollectionUpdate)
}

 componentWillUnmount() {
   this.unsubscribe();
}


onCollectionUpdate = (querySnapshot) => {
  const messages = [];
  // go through each document
  querySnapshot.forEach((doc) => {
    // get the QueryDocumentSnapshot's data
    var data = doc.data();
    messages.push({
      name: data.name,
      items: data.items.toString(),
    });
  });
  this.setState({
    messages,
  });
};

addMessage() { 
  // add a new list to the collection
  this.referenceChatMessages.add({
    name: 'TestList',
    items: ['eggs', 'pasta', 'veggies'],
    uid: this.state.uid,
  });
}


onSend(messages = []) {
  this.setState(previousState => ({
    messages: GiftedChat.append(previousState.messages, messages),
  }))
}

renderBubble(props) {
  return ( 
    <Bubble 
    {...props}
    wrapperStyle={{
      right: {
        backgroundColor: 'blue'
      }
    }}
    />
  )
}

  render() {
    let name = this.props.route.params.name;
    this.props.navigation.setOptions({ title: name });

     return (
    <View className="backgroundStyles" style={styles.container}>   
    <View style={{flex:3, justifyContent: 'center', alignItems: 'center'}}>
    <Text>{this.state.loggedInText}</Text>
    <FlatList
            data={this.state.messages}
            renderItem={({ item }) => 
              <Text style={styles.item}>{item.name}: {item.items}</Text>}
          />   {/* <Button
          title="Go to Start"
          onPress={() => this.props.navigation.navigate('Start')} />    */}
        <GiftedChat 
          renderBubble={this.renderBubble.bind(this)}
          messages={this.state.messages}
          onSend={messages => this.onSend(messages)}
          user={{
            _id: 1,
          }} />
            { Platform.OS === 'android' ? 
            <KeyboardAvoidingView behavior="height" /> : null
            }
    </View>
    </View>
    );
  };
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingTop: 40,      
  },
  item: {
    fontSize: 20,
    color: 'blue',
  },
  text: {
    fontSize: 30,
  }
});
